My_iter = iter(My_list) #先取得物件的迭代器
while True:
    try:
        elem = next(My_iter) #取得元素
        print(elem) #對元素的操作
    except StopIteration:
        #沒有下一個元素的時候觸發會觸發異常，離開迴圈
        break
